function Router() {
  return (
    <Switch>
      <Route path="/" component={HomePage} />
      <Route path="/map" component={MapPage} />
      <Route path="/monitor" component={MonitorPage} />
      <Route path="/impact" component={ImpactPage} />
      <Route component={NotFound} />
